package ifElseConditions;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int age = 20;
		
		if(age>=60) {
			System.out.println();
		}
		
		else if(age>=20 & age<=50) {
			
			System.out.println("Your age is between 20 and 50");
			
		}
		
		else {
			
			System.out.println("You are under 60");
		}

	}

}
